mapper.select:
SELECT COUNT(*) FROM `order`
WHERE status IN ('PENDING_SHIP', 'SHIPPED', 'COMPLETED')
AND pay_time >= #{start} AND pay_time <= #{end}

SELECT COUNT(DISTINCT user_id) FROM `order`
WHERE status IN ('PENDING_SHIP', 'SHIPPED', 'COMPLETED')
AND pay_time >= #{start} AND pay_time <= #{end}

SELECT COALESCE(SUM(total_amount), 0) FROM `order`
WHERE status IN ('PENDING_SHIP', 'SHIPPED', 'COMPLETED')
AND pay_time >= #{start} AND pay_time <= #{end}
